let loginBtn=document.querySelector(".btn--login").addEventListener("click",()=>{let e=document.querySelector("#myUsername").value,t=document.querySelector("#myPassword").value;this.primus=Primus.connect("/",{reconnect:{max:1/0,min:500,retries:10}}),fetch("http://localhost:3000/users/login",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:e,password:t})}).then(e=>e.json()).then(e=>{if("success"===e.status){this.primus.write({action:"liveUser",username:e.data.username});let t=e.data.token,s=e.data.user_id,o=e.data.username;localStorage.setItem("token",t),localStorage.setItem("user_id",s),localStorage.setItem("username",o),window.location.href="http://localhost:3000"}else alert("login failed")})});